import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';

const FeedPage = () => {
  const [content, setContent] = useState('');
  const [postImage, setPostImage] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getPosts();
  }, []);

  const getPosts = async () => {
    try {
      const response = await axios.get('https://localhost:8080/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('Erro ao obter os posts:', error);
    }
  };

const handlePostSubmit = async (e) => {
  e.preventDefault();

  const formData = new FormData();
  formData.append('content', content);
  formData.append('postImage', postImage);

  try {
    const token = localStorage.getItem('token'); // Obtém o token do localStorage
    const response = await axios.post('https://redesocialsenac.azurewebsites.net/posts', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`, // Adiciona o token aos cabeçalhos da solicitação
      },
    });
    // Atualiza a lista de posts após o envio bem-sucedido
    getPosts();
    // Limpa o formulário
    setContent('');
    setPostImage(null);
  } catch (error) {
    console.error('Erro ao criar o post:', error);
    // Exibe a mensagem de erro usando o SweetAlert
    Swal.fire({
      icon: 'error',
      title: 'Erro',
      text: error.response.data,
    });
  }
};


  return (
    <div>
      <h1>Feed</h1>
      <form onSubmit={handlePostSubmit}>
        <label>
          Conteúdo:
          <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
        </label>
        <br />
        <label>
          Imagem:
          <input type="file" onChange={(e) => setPostImage(e.target.files[0])} />
        </label>
        <br />
        <button type="submit">Enviar</button>
      </form>

      <h2>Posts</h2>
      {posts.map((post) => (
        <div key={post.id}>
          <div>
            <h3>{post.username}</h3>
            <img src={`https://redesocialsenac.blob.core.windows.net/posts/${post.image_url}`} alt="Post" />
            <p>{post.content}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
